	/*
	 *Allows you to choose a new class from the market
	 */
	public static int marketClass(){
		Scanner input = new Scanner(System.in);
		String classPick = input.nextLine();
		int num;
		if(classPick.equalsIgnoreCase("fighter")){ //If player selects fighter
			num=1;
		}else if(classPick.equalsIgnoreCase("cleric")){ //If player selects cleric
			num=2;
		}else if(classPick.equalsIgnoreCase("thief")){ //If player selects thief
			num=3;
		}else{
			num=0;
		}
		return(num);
	}
	
	/*
	 *This method lets you choose a person to give a purchased item to
	 */
	public static int choosePerson(String player1, String player2, String player3, String player4){ //The names of the players are passed to the method
		Scanner input = new Scanner(System.in);
		String choice = input.nextLine(); //User enters player name
		int num;
		if(choice.equalsIgnoreCase(player1)){ //Value of num returned is based off what player name is entered
			num=1;
		}else if(choice.equalsIgnoreCase(player2)){
			num=2;
		}else if(choice.equalsIgnoreCase(player3)){
			num=3;
		}else if(choice.equalsIgnoreCase(player4)){
			num=4;
		}else{
			num=0;
		}
		return(num);
	}
	
	/*
	 *This method allows you to enter your market choice
	 */
	public static int market(){
		Scanner input = new Scanner(System.in);
		System.out.print("Enter your choice: ");
		String item = input.nextLine();
		int choice;
		if(item.equalsIgnoreCase("potion")){ //Value of choice returned corresponds to items in the market
			choice=1;
		}else if(item.equalsIgnoreCase("special move")){
			choice=2;
		}else if(item.equalsIgnoreCase("class change")){
			choice=3;
		}else if(item.equalsIgnoreCase("exit")){ //Market is left if player enters "exit"
			choice=4;
		}else if(item.equalsIgnoreCase("revive character")){
			choice=5;
		}else{
			choice=6;
		}
		return(choice);
	}
	 
