    public static void direction(int direction){
    	String n="North"; //These are just to save time typing these out in full
    	String s="South";
    	String e="East";
    	String w="West";
    	String directionDisplay = "Direction: ";
    	if(direction==8){ //Reads direction value
    		System.out.println(directionDisplay+n); //Prints the corresponding direction
    	}else if(direction==6){
    		System.out.println(directionDisplay+e);
    	}else if(direction==2){
    		System.out.println(directionDisplay+s);
    	}else if(direction==4){
    		System.out.println(directionDisplay+w);
    	}else{
    		System.out.println("This wasn't supposed to happen..."); //If the direction values get messed up in case of some bug
    	}
    }
    
