    /*
     *Returns the converted battle move
     */
    public static int battleMove(){
    	Scanner input = new Scanner(System.in);
    	String move = input.nextLine(); //User inputs move
    	int convert=0;
    	if(move.equalsIgnoreCase("attack")){ //Value of convert depends on what command is entered
    		convert=1;
    	}else if(move.equalsIgnoreCase("heal")){
    		convert=2;
    	}else if(move.equalsIgnoreCase("special attack")){
    		convert=3;
    	}else if(move.equalsIgnoreCase("quit")){ //If "quit" is entered during battle, the program ends immediately
    		System.exit(0);
    	}
    	return(convert);
    }
    
