	/*
	 *This method takes input from the user
	 */
	public static int input(){
		Scanner input = new Scanner(System.in);
		int move=0;
		while(true){
			System.out.print("Enter your command: ");
			String command = input.nextLine();
			if(command.equalsIgnoreCase("w")){
				move=1;
				break;
			}else if(command.equalsIgnoreCase("a")){
				move=2;
				break;
			}else if(command.equalsIgnoreCase("d")){
				move=3;
				break;
			}else if(command.equalsIgnoreCase("quit") || command.equalsIgnoreCase("kys")){
				System.exit(0);
			}else{
				move=4;
				break;
			}
		}
		return(move);
	}
	
	/*
	 *This method allows the user to change direction
	 */
	public static int direction(int direction, int move){
		if(direction==8 && move==2){
			direction=4;
		}else if(direction==8 && move==3){
			direction=6;
		}else if(direction==6 && move==2){
			direction=8;
		}else if(direction==6 && move==3){
			direction=2;
		}else if(direction==2 && move==2){
			direction=6;
		}else if(direction==2 && move==3){
			direction=4;
		}else if(direction==4 && move==2){
			direction=2;
		}else if(direction==4 && move==3){
			direction=8;
		}
		return(direction);
	}
	
